import React from 'react';
import NavigationBar from '../components/NavigationBar';
import Menu from '../components/Menu'
import './Home.scss'

class Home extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      showMenu: false
    };
  }

  handleMenuClick() {
    this.setState(state => ({
      showMenu: !state.showMenu
    }));
  }

  render() {
    return (
      <div id="home">
        <NavigationBar onMenuClick={() => this.handleMenuClick()} />
        <Menu show={this.state.showMenu} />
      </div>
    );
  }
}

export default Home;
