import React from 'react';
import './Menu.scss';

class Menu extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      opacity: 0
    };
  }

  componentDidMount() {
    const timePerOpacity = 10;

    this.timerId = setInterval(() => {
      this.setState(state => ({
        opacity: state.opacity + 0.01
      }));
    }, timePerOpacity);

    setTimeout(() => clearInterval(this.timerId), timePerOpacity * 100);
  }

  render() {
    return (
      <div className="lm-menu-box">
        <div className="lm-menu-backdrop"></div>
        <div className="lm-menu-content"></div>
      </div>
    );
  }
}

export default Menu;
