import React from 'react';
import './Menu.scss';

class Menu extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      backdropClass: 'hidden'
    };
  }

  componentDidMount() {
    setTimeout(() => this.setState({ backdropClass: 'visible' }), 0);
  }

  render() {
    return (
      <div className={"lm-menu-box"} >
        <div className={"lm-menu-backdrop " + this.state.backdropClass}></div>
        <div className="lm-menu-content"></div>
      </div>
    );
  }
}

export default Menu;
