import React from 'react';
import './Menu.scss';

class Menu extends React.Component {
  render() {
    let hiddenOrNot = (this.props.show ? "visible" : "hidden");

    return (
      <div className={"lm-menu-box " + (this.props.show ? "" : "hidden")}>
        <div className={"lm-menu-backdrop " + (this.props.show ? "" : "transparent")}></div>
        <div className="lm-menu-content"></div>
      </div>
    );
  }
}

export default Menu;
