import React from 'react';
import {delayUnmounting} from '../utils/utils';
import './Menu.scss';

class Menu extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      backdropClass: 'transparent'
    };
  }

  componentDidMount() {
    setTimeout(() => this.setState({ backdropClass: '' }), 0);
  }

  componentWillUnmount() {
    setTimeout(() => this.setState({ backdropClass: 'transparent' }), 0);
  }

  render() {
    let backdropClass = '';
    if (!this.props.isMounted) {
      backdropClass = 'transparent';
    }

    return (
      <div className={"lm-menu-box"} >
        <div className={"lm-menu-backdrop " + this.state.backdropClass}></div>
        <div className="lm-menu-content"></div>
      </div>
    );
  }
}

export default delayUnmounting(Menu);
